import { Neat } from '@firecms/neat';
import * as THREE from 'three'; // Import three.js

// Wait for the DOM to load
window.onload = () => {
  // Select the container for the gradient
  const container = document.getElementById('gradient-container');

  // Define the gradient configuration
  const config = {
    colors: [
      { color: '#43008E', enabled: true },
      { color: '#1E0062', enabled: true },
      { color: '#000000', enabled: true },
      { color: '#8B6AE6', enabled: true },
      { color: '#2101AB', enabled: true },
    ],
    speed: 2.5,
    horizontalPressure: 3,
    verticalPressure: 4,
    waveFrequencyX: 3,
    waveFrequencyY: 3,
    waveAmplitude: 4,
    shadows: 1,
    highlights: 5,
    colorBrightness: 1.1,
    colorSaturation: 4,
    wireframe: true,
    colorBlending: 10,
    backgroundColor: '#000000',
    backgroundAlpha: 1,
    grainScale: 3,
    grainSparsity: 0.02,
    grainIntensity: 0.3,
    grainSpeed: 1,
    resolution: 1,
  };

  // Initialize Neat
  const gradient = new Neat(container, config);

  // Start the gradient animation
  gradient.start();
};
